import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Header } from './components/Header/Header';
import { TodoCreation } from './components/TodoCreation/TodoCreation';
import { TodoList } from './components/TodoList/TodoList';
import { TodoItem } from './components/TodoItem/TodoItem';
import { TodoFilter } from './components/TodoFilter/TodoFilter';

const defaultTodo = [
  { text: 'Complete online JavaScript course', completed: true },
  { text: 'Jog around the park 3x', completed: false },
  { text: 'Read for 1 hour', completed: false },
  { text: 'Pick up groceries', completed: false },
  { text: 'Complete Todo App on Frontend Mentor', completed: false },
];

const App = () => {
  const [todos, setTodos] = useState(defaultTodo);
  const [filter, setFilter] = useState('all');

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const updatedTodos = Array.from(todos);
    const [movedTodo] = updatedTodos.splice(result.source.index, 1);

    updatedTodos.splice(result.destination.index, 0, movedTodo);

    setTodos(updatedTodos);
  };

  const handleTodoToggle = (todoText) => {
    const updatedTodos = todos.map((todo) =>
      todo.text === todoText ? { ...todo, completed: !todo.completed } : todo
    );
    setTodos(updatedTodos);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  const filteredTodos = todos.filter((todo) => {
    if (filter === 'active') {
      return !todo.completed;
    } else if (filter === 'completed') {
      return todo.completed;
    }
    return true;
  });

  return (
    <>
      <Header />
      <TodoCreation />
      <TodoList>
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="todo-list">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef}>
                {filteredTodos.map((todo, index) => (
                  <Draggable
                    key={todo.text}
                    draggableId={todo.text}
                    index={index}
                  >
                    {(provided) => (
                      <TodoItem
                        text={todo.text}
                        completed={todo.completed}
                        toggleTodo={handleTodoToggle}
                        provided={provided}
                      />
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </TodoList>
      <TodoFilter
        completed={todos.filter((todo) => todo.completed).length}
        total={todos.length}
        handleFilterChange={handleFilterChange}
      />
    </>
  );
};

export default App;
