import React, { useState } from 'react';
import { Header } from './Header';
import { TodoCreation } from './TodoCreation';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { TodoFilter } from './TodoFilter';

const defaultTodo = [
  { text: 'Complete online JavaScript course', completed: true },
  { text: 'Jog around the park 3x', completed: false },
  { text: 'Read for 1 hour', completed: false },
  { text: 'Pick up groceries', completed: false },
  { text: 'Complete Todo App on Frontend Mentor', completed: false },
];

function App() {
  const [todos, setTodos] = useState(defaultTodo);

  // Funci贸n para manejar el evento de soltar (drop)
  const handleDrop = (event) => {
    event.preventDefault();
    const droppedTodo = JSON.parse(event.dataTransfer.getData('text/plain'));
    const updatedTodos = todos.map((todo) => {
      if (todo.text === droppedTodo.text) {
        return {
          ...todo,
          completed: !todo.completed,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  // Funci贸n para manejar el evento de arrastrar sobre (drag over)
  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <>
      <Header />
      <TodoCreation />
      <TodoList
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        {todos.map((todo) => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
            draggable
            onDragStart={(event) => {
              event.dataTransfer.setData('text/plain', JSON.stringify(todo));
            }}
          />
        ))}
      </TodoList>
      <TodoFilter completed={''} total={''} />
    </>
  );
}

export default App;
